import { useState } from 'react';
import { useGeolocated } from 'react-geolocated';

function App(props: useGeolocated) {
  const [count, setCount] = useState(
    Number(window.localStorage.getItem('count')) || 0
  );
  const [countryCounts, setCountryCounts] = useState({});


  const handleClick = () => {
    const newCount = count + 1;
    setCount(newCount);
    window.localStorage.setItem('count', newCount);

    if (props.isGeolocationAvailable && props.isGeolocationEnabled) {
      const country = props?.geolocation?.country ?? 'Unknown';
      setCountryCounts(prevCounts => ({
        ...prevCounts,
        [country]: (prevCounts[country] ?? 0) + 1,
      }));
    }
  };

  {Object.entries(countryCounts).length > 0 && (
    <div>
      <h2>Click distribution by country:</h2>
      <ul>
        {Object.entries(countryCounts).map(([country, count]) => (
          <li key={country}>{country}: {count} clicks</li>
        ))}
      </ul>
    </div>
  )}

  return (
    <div className="App">
      <h1>Click Counter</h1>
      <button onClick={handleClick}>Click me!</button>
      <p>Click count: {count}</p>
    </div>
  );

}

export default geolocated()(App);
