const express = require('express');
const app = express();
const port = 5000;

app.use(express.json());

app.post('/api/clicks', async (req, res) => {
  const { location } = req.body;

  try {
    // save the location data to the database
    res.sendStatus(200);
  } catch (err) {
    console.error(err);
    res.sendStatus(500);
  }
});

app.listen(port, () => {
  console.log(`Server listening on port ${port}`);
});
